import React from 'react';
import "./home.css";
import download from "./../../images/download.jfif";
import download1 from "./../../images/download (15).jfif"

import { useState, useEffect } from 'react';

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function Home() {
    
    const { height, width } = useWindowDimensions();

    const featured_article_height = height * (40 / 100);

    const featured_article_width = 9.5 / 10 * (width);

    const featured_article_left_side_width = featured_article_width * (65 / 100);

    const featured_article_right_side_width = featured_article_width * (25 / 100);

    const big_image_width = featured_article_left_side_width * (95/100);

    const small_image_width = featured_article_right_side_width * (95 / 100);
    
    // console.log(width, height, featured_article_width)

    return (
        <div>

            <div style={{ "width": featured_article_width, "height": featured_article_height }} className="featured-articles">
                
                <div className="big-picture" style={{"width" : featured_article_left_side_width}}>
                    <img src={download1} style={{"width" : big_image_width}} alt="none" />
                </div>

                <div className="two-images" style={{ "width": featured_article_right_side_width }}>
                    
                    <div className="image1">
                        <img src={download} alt="none" style={{"width" : small_image_width}}/>
                    </div>

                    <div className="image2">
                        <img src={download} alt="none" style={{"width" : small_image_width}}/>
                    </div>

                </div>
            </div>
            
            <div className="latest">
                <h2>The Latest</h2>
                <hr />
                <div className="latest-articles-sections"></div>
            </div>

            <div className="bolly-div">
                <h2>Latest Articles: Bollywood</h2>
                <hr />
                <div className="latest-articles-bolly"></div>
            </div>

            <div className="top-posts-div">
                <h2>Top Posts</h2>
                <hr />
                <div className="top-posts"></div>
            </div>

            <div className="holly-div">
                <h2>Latest Articles: Hollywood</h2>
                <hr />
                <div className="latest-articles-holly"></div>
            </div>

            <div className="tech-div">
                <h2>Latest Articles: Technology</h2>
                <hr />
                <div className="latest-articles-tech"></div>
            </div>

        </div>
    )
};

export default Home;
