import React from 'react';
import "./Article.css";
import "./css/all.min.css";
import { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHands, faHatCowboy, faShare, faSnowman } from '@fortawesome/free-solid-svg-icons'

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function Article() {
    
    const { height, width } = useWindowDimensions();

    // isLoggedIn ? { display:'block'} : {display : 'none'}

    return (
        <div style={{ width:window.innerWidth > 1400 && window.innerWidth <= 1600 ? {width:width*(8/10)}:{width:width} }}>
        {/* <div className="article-area" style={{ "display": "flex", "flexDirection": "row", "flexDirection": window.innerWidth > 1400 && window.innerWidth <= 1600 ? { "flexDirection": "row" } : { "flexDirection": "columnReverse" } }}> */}
        <div className={window.innerWidth > 1400 ? "for-flex-row" : "for-flex-columnReverse"}>
          <div className="clap-and-share" style={{ width: window.innerWidth > 1400 && window.innerWidth <= 1600 ?   {width: width * (3 / 10) } : { width: width } }}>
                  <FontAwesomeIcon icon={faHands}></FontAwesomeIcon>
                  <FontAwesomeIcon icon={faShare}></FontAwesomeIcon>          
          </div>
          <div className="main-article-area" style={{ width: window.innerWidth > 1400 && window.innerWidth <= 1600 ? { width: width * (5 / 10) } : { width: width } }}>
            <h2>5 Ways to animate a React app.</h2>
            <div className="author-and-icons">
              <div className="author">
                <div className="author-pic">
                  <FontAwesomeIcon icon={faSnowman}></FontAwesomeIcon>
                </div>
                <div className="author-name-and-date-and-time-to-read">
                  <h3>Yogesh Kumar</h3>
                  <p>Nov 19, 2021 10 min read</p>
                </div>
              </div>
              <div className="icons"></div>
            </div>
          </div>
        </div>
            <div className="suggestion-area"></div>
        </div>
    )
}

export default Article;
