import React from 'react';
import { Link } from 'react-router-dom';
import "./home.css";
import download from "./../../images/download.jfif";
import download1 from "./../../images/download (15).jfif";
import Couple from "./../../images/couple.jpg";

import { useState, useEffect } from 'react';
import LatestArticlesCards from '../cards/LatestArticleCards/LatestArticlesCards';
import BollyCards from '../cards/BollyCards/BollyCards';
import TopPostsCards from '../cards/TopPostsCards/TopPostsCards';
import HollyCards from '../cards/HollyCards/HollyCards';
import TechCards from '../cards/TechCards/TechCards';


function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function Home() {
    
    const { height, width } = useWindowDimensions();

    const featured_article_height = height * (65 / 100);

    const featured_article_left_side_height = featured_article_height * (95/100) ;
    const featured_article_right_side_height = featured_article_height * (95/100) ;

    const featured_article_width = 7.8 / 10 * (width);

    const featured_article_left_side_width = featured_article_width * (65 / 100);
    // const featured_article_left_side_image_height = featured_article_left_side_width * (95/100) ;

    const featured_article_right_side_width = featured_article_width * (33 / 100);
    const featured_article_right_side_images_height = featured_article_right_side_height * (48/100) ;

    // const big_image_width = featured_article_left_side_width * (95/100);
    const big_image_width = featured_article_left_side_width;

    // const small_image_width = featured_article_right_side_width * (95 / 100);
    const small_image_width = featured_article_right_side_width;
    
    // console.log(width, height, featured_article_width)

    return (
        <div>

            
        <picture>
          <source
            srcset={Couple}
            type="image/jpg"
            media="all and (max-width:600px)"
          />
          <source
            srcset={Couple}
            type="image/jpg"
            media="all and (min-width:601px) and (max-width:1200px)"
          />
          <source
            srcset={Couple}
            type="image/jpg"
            media="all and (min-width:1201px)"
          />
          {/* <!-- the default --> */}
          <img src={Couple} alt="couple in field" />
        </picture>

            <div style={{ "width": featured_article_width }} className="featured-articles">
                
                <Link to="/article"><div className="big-picture" style={{"width" : featured_article_left_side_width, "height": featured_article_left_side_height}}>
                    <img src={download1} alt="none" />
                    <div style={{"position": "absolute",
  "bottom": featured_article_left_side_height*(20/100),
  "left":featured_article_left_side_width*(20/100)}}>
                        <div className="text-title">
                            <h2 style={{ "color": "white" }}>Title of vertical gallary</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                </div></Link>

                <div className="two-images" style={{ "width": featured_article_right_side_width, "height": featured_article_right_side_height }}>
                    
                    <Link to="/article"><div className="image1">
                        <img src={download} alt="none" style={{"height": featured_article_right_side_images_height }}/>
                    <div style={{"position": "absolute",
  "top": featured_article_right_side_height*(10/100),
  "left":featured_article_right_side_width*(15/100)}}>
                        <div className="text-title">
                                <h2 style={{ "color": "white", "marginBottom":"0" }}>The Sound cloud</h2>
                                <h2 style={{ "color": "white", "marginTop":"2px" }}>You Loved is doomed</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                    </div></Link>

                    <Link to="/article"><div className="image2">
                        <img src={download} alt="none" style={{"height": featured_article_right_side_images_height }}/>
                    <div style={{"position": "absolute",
  "bottom": featured_article_right_side_height*(10/100),
  "left":featured_article_right_side_width*(15/100)}}>
                        <div className="text-title">
                            <h2 style={{ "color": "white", "marginBottom":"0" }}>The Sound cloud</h2>
                            <h2 style={{ "color": "white", "marginTop":"2px" }}>You Loved is doomed</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                    </div></Link>

                </div>
            </div>
            
            <div className="latest">
                <h2>The Latest</h2>
                <hr />
                <div className="latest-articles-sections" style={{"width": featured_article_width }}>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px" }}>{<LatestArticlesCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<LatestArticlesCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<LatestArticlesCards />}</div></Link>
                </div>
            </div>

            <div className="bolly-div">
                <h2>Latest Articles: Bollywood</h2>
                <hr />
                <div className="latest-articles-bolly" style={{"width": featured_article_width }}>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px" }}>{<BollyCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<BollyCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{ "width": featured_article_width * (30 / 100), "minWidth": "300px" }}>{<BollyCards />}</div></Link>
                </div>
            </div>

            <div className="top-posts-div">
                <h2>Top Posts</h2>
                <hr />
                <div className="top-posts" style={{"width": featured_article_width }}>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px" }}>{<TopPostsCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<TopPostsCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{ "width": featured_article_width * (30 / 100), "minWidth": "300px" }}>{<TopPostsCards />}</div></Link>
                </div>
            </div>

            <div className="holly-div">
                <h2>Latest Articles: Hollywood</h2>
                <hr />
                <div className="latest-articles-holly" style={{"width": featured_article_width }}>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px" }}>{<HollyCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<HollyCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{ "width": featured_article_width * (30 / 100), "minWidth": "300px" }}>{<HollyCards />}</div></Link>
                    </div>
            </div>

            <div className="tech-div">
                <h2>Latest Articles: Technology</h2>
                <hr />
                <div className="latest-articles-tech" style={{"width": featured_article_width }}>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px" }}>{<TechCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{"width": featured_article_width*(30/100), "minWidth": "300px"  }}>{<TechCards />}</div></Link>
                    <Link to="/article"><div className="cards" style={{ "width": featured_article_width * (30 / 100), "minWidth": "300px" }}>{<TechCards />}</div></Link>
                </div>
            </div>

        </div>
    )
};

export default Home;
