import React from 'react';
import "./home.css";
import download from "./../../images/download.jfif";
import download1 from "./../../images/download (15).jfif"

import { useState, useEffect } from 'react';
import LatestArticlesCards from '../cards/LatestArticleCards/LatestArticlesCards';
import BollyCards from '../cards/BollyCards/BollyCards';
import TopPostsCards from '../cards/TopPostsCards/TopPostsCards';
import HollyCards from '../cards/HollyCards/HollyCards';
import TechCards from '../cards/TechCards/TechCards';


function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function Home() {
    
    const { height, width } = useWindowDimensions();

    const featured_article_height = height * (65 / 100);

    const featured_article_left_side_height = featured_article_height * (95/100) ;
    const featured_article_right_side_height = featured_article_height * (95/100) ;

    const featured_article_width = 7.8 / 10 * (width);

    const featured_article_left_side_width = featured_article_width * (65 / 100);
    // const featured_article_left_side_image_height = featured_article_left_side_width * (95/100) ;

    const featured_article_right_side_width = featured_article_width * (33 / 100);
    const featured_article_right_side_images_height = featured_article_right_side_height * (48/100) ;

    // const big_image_width = featured_article_left_side_width * (95/100);
    const big_image_width = featured_article_left_side_width;

    // const small_image_width = featured_article_right_side_width * (95 / 100);
    const small_image_width = featured_article_right_side_width;
    
    // console.log(width, height, featured_article_width)

    return (
        <div>

            <div style={{ "width": featured_article_width }} className="featured-articles">
                
                <div className="big-picture" style={{"width" : featured_article_left_side_width, "height": featured_article_left_side_height}}>
                    <img src={download1} style={{ "width": big_image_width }} alt="none" />
                    <div style={{"position": "absolute",
  "bottom": featured_article_left_side_height*(20/100),
  "left":featured_article_left_side_width*(20/100)}}>
                        <div className="text-title">
                            <h2 style={{ "color": "white" }}>Title of vertical gallary</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                </div>

                <div className="two-images" style={{ "width": featured_article_right_side_width, "height": featured_article_right_side_height }}>
                    
                    <div className="image1">
                        <img src={download} alt="none" style={{"height": featured_article_right_side_images_height }}/>
                    <div style={{"position": "absolute",
  "top": featured_article_right_side_height*(10/100),
  "left":featured_article_right_side_width*(10/100)}}>
                        <div className="text-title">
                            <h2 style={{ "color": "white" }}>The Sound cloud You Loved is doomed</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                    </div>

                    <div className="image2">
                        <img src={download} alt="none" style={{"height": featured_article_right_side_images_height }}/>
                    <div style={{"position": "absolute",
  "bottom": featured_article_right_side_height*(10/100),
  "left":featured_article_right_side_width*(10/100)}}>
                        <div className="text-title">
                            <h2 style={{ "color": "white" }}>Title of vertical gallary</h2>
                            <p style={{ "fontWeight": "100", "color":"rgb(165, 164, 164)" }}>Travel / August 2017</p>
                        </div>
                    </div>
                    </div>

                </div>
