import React from 'react';
import "./Article.css";
import "./css/all.min.css";
import { useState, useEffect } from 'react';
import { SocialIcon } from 'react-social-icons';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faCamera, faCameraRetro, faHands, faShare, faSnowman, faVideo } from '@fortawesome/free-solid-svg-icons';


// importing images 
import ClapIcon from "./../../images/rythm.png";
import ShareIcon from "./../../images/share.png";
import ManIcon from "./../../images/Mask Group 16.png";
import ReactImage from "./../../images/reactjsanimation.png";
import ReactCode from "./../../images/reactcode.png";


// for load more
import LoadMore from "./loadmore";
import posts from "./postsArray";


function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}

function Article() {

  const [items, setItems] = useState([]);
  const [visible, setVisible] = useState(3);

  const showMoreItems=()=>{
    setVisible((previous) => previous + 3)
    console.log(items.slice(0, 3))
  };

  useEffect(()=>{
    fetch("https://jsonplaceholder.typicode.com/posts").then(res=>res.json()).then((data)=>(setItems(data)));
  }, [])

// const postsPerPage = 3;
//   let arrayForHoldingPosts = [];
  
  const [claps, setClaps] = useState("9.3")

// const [postsToShow, setPostsToShow] = useState([]);
// const [next, setNext] = useState(3);

// const loopWithSlice = (start, end) => {
// const slicedPosts = posts.slice(start, end);
// arrayForHoldingPosts = [...arrayForHoldingPosts, ...slicedPosts];
//     setPostsToShow(arrayForHoldingPosts);
//   };

// useEffect(() => {
//     loopWithSlice(0, postsPerPage);
//   }, []);

// const handleShowMorePosts = () => {
//     loopWithSlice(next, next + postsPerPage);
//     setNext(next + postsPerPage);
//   };
    
    const { height, width } = useWindowDimensions();

    // isLoggedIn ? { display:'block'} : {display : 'none'}

    return (
        <div style={{ width:window.innerWidth > 1400 && window.innerWidth <= 1600 ? {width:width*(8/10)}:{width:width} }}>
        {/* <div className="article-area" style={{ "display": "flex", "flexDirection": "row", "flexDirection": window.innerWidth > 1400 && window.innerWidth <= 1600 ? { "flexDirection": "row" } : { "flexDirection": "columnReverse" } }}> */}
        <div className={window.innerWidth > 600 ? "for-flex-row-clap" : ""}>
          <div className="clap-and-share" style={{ width: window.innerWidth > 1400 && window.innerWidth <= 1600 ?   {width: width * (3 / 10) } : { width: width } }}>
            <div style={{"margin":"auto"}} className={window.innerWidth > 600 ? "for-clap-and-share-fix-position" : ""}>
              <div>
                <img style={{ width: "40px", height: "40px", "float": "left" }} src={ClapIcon} alt="" />
                <div style={{ "display": "inline-block", "margin": "auto", "color": "gray", "marginTop": "10px" }}>{claps}k claps </div>
              </div>
              <br />
              <div>
                <img style={{ width: "40px", height: "40px", "float": "left" }} src={ShareIcon} alt="" />
                <div style={{ "display": "inline-block", "margin": "auto", "color": "gray", "marginTop": "10px" }}>Share this article</div>
              </div>
            </div>
          </div>
          <div className="main-article-area" style={{ width: window.innerWidth > 1400 && window.innerWidth <= 1600 ? { width: width * (5 / 10) } : { width: width } }}>
            <h2>5 Ways to animate a React app.</h2>
            <div className={window.innerWidth > 600 ? "for-flex-row" : "for-flex-columnReverse"}>
              <div className="author">
                <div className={"author-pic"}>
                  <div style={{"margin":"auto"}}>
                    <img src={ManIcon} alt="" />
                  </div>
                </div>
                <div style={{ "margin":"auto", "marginLeft":"20px", "fontSize":"10px" }} className="author-name-and-date-and-time-to-read">
                  <h3>Yogesh Kumar</h3>
                  <p>Nov 19, 2021 10 min read</p>
                </div>
              </div>
              <div className="icons">
                <span><SocialIcon style={{ width: "20px", height: "20px", "margin":"0px 3px" }} url="https://facebook.com/jaketrent" /></span>
                <span><SocialIcon style={{ width: "20px", height: "20px", "margin":"0px 3px" }} url="https://twitter.com/jaketrent" /></span>
                <span><SocialIcon style={{ width: "20px", height: "20px", "margin":"0px 3px" }} url="https://instagram.com/jaketrent" /></span>
                <span><SocialIcon style={{width:"20px", height:"20px"}} url="https://youtube.com/jaketrent" /></span>
              </div>
            </div>
            <div style={{ "marginTop":"10px"}}>
              <img src={ReactImage} alt="" />
            </div>
            <div>
              <p>Animation in ReactJs app is a popular topic and there are many ways to create different types of animations.Many developers create animation exclusively using css and adding classes to HTML tags. This is a great way and you should use it. If you want to create complex animations you can pay attention to GreenSock. GreenSock is the most powerful animation platform. There are also a lot of libraries, components for creating animation in React.</p>
              <p>Letâ€™s talk about them</p>
              <div>
                <img src={ReactCode} alt="" />
              </div>
              <div className="tags">
                <div className="tag">React</div>
                <div className="tag">JavaScript</div>
                <div className="tag">Animation</div>
              </div>
              <div className="clap">
                <img style={{ width: "40px", height: "40px", "float": "left" }} src={ClapIcon} alt="" />
                <div style={{ "display":"inline-block","margin":"auto", "color":"gray","marginTop":"10px"}}>{ claps }k claps </div>
              </div>
            </div>
          </div>
        </div>
        <div style={{"backgroundColor":"white", "zindex":"1"}}>
            <h3 style={{"marginTop":"10px", "fontFamily":"Arial, Helvetica, sans-serif", "marginLeft":"0px", "paddingLeft":"0px", "textAlign":"left"}}>More from Yogesh</h3>
            <hr style={{ "border":"1px solid rgb(219, 219, 219)", "borderRadius":"2px", height:"0.5px", "backgroundColor":"rgb(219, 219, 219)", width:"100%" }}/>
        </div>
        <div className="container">
          {items.slice(0, visible).map((item)=>{
            <div className="card">
              <div className="id">
                <span>id: {item.id}</span>
              </div>
                <p>body:{item.body}</p>
            </div>
          })}
          <button onClick={showMoreItems}>Load More</button>
        </div>
      </div>
    )
}

export default Article;
