import React from 'react';
import Landscape from "./../../images/landscape.jpg";
import "./experiment.css";
import { useState, useEffect } from "react";

function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height
  };
}

function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return windowDimensions;
}
    

function Experiment() {

    const { height, width } = useWindowDimensions();

    const containerWidth = width * (9 / 10);
    const bigImageWidth = containerWidth * (65 / 100);
    const twoImagesContainerWidth = containerWidth * (30 / 100);
    const smallImageWidth = twoImagesContainerWidth;

    return (
        <div className="my-images" style={{width:containerWidth}}>
            <div className="big-image">
                <img style={{width:bigImageWidth}} src={Landscape} alt="" />
            </div>
            <div className="two-images">
                <div className="small-image1">
                    <img style={{width:smallImageWidth}} src={Landscape} alt="" /></div>
                <div className="small-image2">
                <img style={{width:smallImageWidth}} src={Landscape} alt="" /></div>
            </div>
        </div>
    )
}

export default Experiment;
